import { useState } from "react";
import axios from "axios";

const API_BASE = process.env.REACT_APP_API_URL?.replace(/\/$/, '') || '';

export default function AddUser({ onAdded }) {
  const [form, setForm] = useState({ name: "", email: "" });
  const [saving, setSaving] = useState(false);

  const change = e => setForm({ ...form, [e.target.name]: e.target.value });

  const submit = async e => {
    e.preventDefault();
    setSaving(true);
    try {
  await axios.post(`${API_BASE}/users`, form);
      onAdded?.();               // yêu cầu reload list
      setForm({ name: "", email: "" });
    } catch (e) {
      alert(e?.response?.data?.message || "Thêm user thất bại");
    } finally {
      setSaving(false);
    }
  };

  return (
    <form onSubmit={submit} style={{ display:"grid", gap: 8, maxWidth: 360 }}>
      <h2>Thêm User</h2>
      <input name="name" placeholder="Tên" value={form.name} onChange={change} required />
      <input name="email" type="email" placeholder="Email" value={form.email} onChange={change} required />
      <button type="submit" disabled={saving}>{saving ? "Đang lưu..." : "Thêm"}</button>
    </form>
  );
}
